import {
  sendPasswordResetEmail,
  signInWithEmailAndPassword,
} from "firebase/auth";
import React, { useEffect, useRef, useState } from "react";
import { auth, db, provider } from "../config/firebase";
import { signInWithPopup } from "firebase/auth";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import "../assets/styles/signin_signup/signin_signup.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {
  Timestamp,
  collection,
  doc,
  getDoc,
  getDocs,
  onSnapshot,
  query,
  setDoc,
  where,
} from "firebase/firestore";
import axios from "axios";
import { BASE_URL } from "../config/mongo";

const SignIn = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  // const [passwordError, setPasswordError] = useState("");
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [isEmailValid, setIsEmailValid] = useState(true);
  const [isPasswordValid, setIsPasswordValid] = useState(true);

  const signInRef = useRef(null);

  useEffect(() => {
    function handleKeyDown(event) {
      if (event.code === "Enter") {
        signInRef.current.click();
      }
    }

    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  });
  // useEffect(() => {
  //   // console.log("message");
  // }, [passwordError]);

  function handlePasswordChange(e) {
    if (password.length < 8) {
      setPasswordError("Password must be at least 8 characters long");
    } else {
      setPasswordError("");
    }
  }

  const handleSignIn = (e) => {
    e.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        navigate("/");
      })
      .catch((err) => {
        setIsEmailValid(true);
        setIsPasswordValid(true);
        // console.log(error);
        if (email === "") {
          setEmailError("Field is empty");
        } else {
          switch (err.code) {
            case "auth/user-not-found":
              setEmailError("This email address does not exist");
              setIsEmailValid(false);
              break;
            case "auth/invalid-email":
              setEmailError("This email address does not exist");
              setIsEmailValid(false);
              break;
            case "auth/wrong-password":
              setPasswordError("Wrong password");
              setIsPasswordValid(false);
              break;
            default:
              setEmailError(
                "this account has been temporarily disabled due to many failed login attempts. You can immediately restore it by resetting your email or you can try again later"
              );
              setPasswordError(
                "this account has been temporarily disabled due to many failed login attempts. You can immediately restore it by resetting your password or you can try again later"
              );
          }
        }
      });
    handlePasswordChange();
  };

  const checkUserCredentials = async (user) => {
    try {
      // const userDoc = await getDoc(doc(db, "Users", user.uid));
      const response = await axios.get(`${BASE_URL}/check-user/${user.uid}`);
      if (response.data) {
        return true;
      } else {
        return false;
      }
    } catch (error) {
      console.log("Error checking user credentials:", error);
      return false;
    }
  };

  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      console.log("USER", user);
      const userExists = await checkUserCredentials(user);
      if (!userExists) {
        // const GoogleData = {
        //   uid: user.uid,
        //   username: user.displayName,
        //   bio: "",
        //   interests: [],
        //   folders: [],
        //   photo: user.photoURL,
        //   isAdmin: false,
        //   timestamp: Timestamp.now(),
        // };
        const FullName = user.displayName;
        const uid = user.uid;
        const photo = user.photoURL;
        const response = await axios.post(`${BASE_URL}/signup`, {
          FullName,
          uid,
          photo,
        });
        navigate("/");
      } else {
        navigate("/");
      }
    } catch (error) {
      console.log(error);
    }
  };
  const handleForgotPassword = () => {
    sendPasswordResetEmail(auth, email)
      .then(() => {
        // Password reset email sent!
        console.log("Password reset email sent to " + email);
        toast.success(" ðŸ‘‰An email has been sent to you", {
          position: "top-center",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log("ERROR : " + errorCode + " " + errorMessage);
        toast.error(
          "An error occurred. Please check if the email is correct.",
          {
            position: "top-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "colored",
          }
        );
      });
  };

  return (
    <div className="sign-in h-full bg-[#] w-full h-[100%] py-16 px-6">
      <div className="flex flex-col items-center justify-center">
        <div className=" bg-[#1A1B1F] shadow rounded-[15px] max-h-[900px] max-w-[505px] w-full py-10 px-[60px]">
          <div className="flex flex-column mb-6 align-items-center ">
            <div
              tabIndex={0}
              aria-label="Login to your account"
              className="text-[24px] poppins-medium text-white opacity-[.96]"
            >
              Welcome Back
            </div>
          </div>

          <div className="flex flex-column gap-2">
            <lable className="text-[15px] poppins-light leading-none text-white">
              E-mail
            </lable>
            <input
              onChange={(e) => setEmail(e.target.value)}
              aria-label="enter email adress"
              type="email"
              className="bg-gray-200  border rounded focus:outline-none text-xs font-medium leading-none text-gray-800 py-3 max-w-[373] h-[36px] pl-3 mt-2"
              placeholder="Enter your email"
            />
          </div>

          {!isEmailValid ? (
            <div className="text-danger my-2">{emailError}</div>
          ) : null}

          <div className="mt-6  w-full">
            <lable className="text-[15px] poppins-light leading-none text-white">
              Password
            </lable>
            <div className="relative flex items-center justify-center">
              <input
                onChange={(e) => setPassword(e.target.value)}
                aria-label="enter Password"
                type={isPasswordVisible ? "text" : "password"}
                className="bg-gray-200 border rounded focus:outline-none text-xs font-medium leading-none text-gray-800 py-3 w-full h-[36px] pl-3 mt-2"
                placeholder="Enter your password"
              />
              <div
                onClick={() => {
                  setIsPasswordVisible(!isPasswordVisible);
                }}
                className="absolute right-0 mt-2 mr-3 cursor-pointer"
              >
                <svg
                  width={16}
                  height={16}
                  viewBox="0 0 16 16"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M7.99978 2C11.5944 2 14.5851 4.58667 15.2124 8C14.5858 11.4133 11.5944 14 7.99978 14C4.40511 14 1.41444 11.4133 0.787109 8C1.41378 4.58667 4.40511 2 7.99978 2ZM7.99978 12.6667C9.35942 12.6664 10.6787 12.2045 11.7417 11.3568C12.8047 10.509 13.5484 9.32552 13.8511 8C13.5473 6.67554 12.8031 5.49334 11.7402 4.64668C10.6773 3.80003 9.35864 3.33902 7.99978 3.33902C6.64091 3.33902 5.32224 3.80003 4.25936 4.64668C3.19648 5.49334 2.45229 6.67554 2.14844 8C2.45117 9.32552 3.19489 10.509 4.25787 11.3568C5.32085 12.2045 6.64013 12.6664 7.99978 12.6667ZM7.99978 11C7.20413 11 6.44106 10.6839 5.87846 10.1213C5.31585 9.55871 4.99978 8.79565 4.99978 8C4.99978 7.20435 5.31585 6.44129 5.87846 5.87868C6.44106 5.31607 7.20413 5 7.99978 5C8.79543 5 9.55849 5.31607 10.1211 5.87868C10.6837 6.44129 10.9998 7.20435 10.9998 8C10.9998 8.79565 10.6837 9.55871 10.1211 10.1213C9.55849 10.6839 8.79543 11 7.99978 11ZM7.99978 9.66667C8.4418 9.66667 8.86573 9.49107 9.17829 9.17851C9.49085 8.86595 9.66644 8.44203 9.66644 8C9.66644 7.55797 9.49085 7.13405 9.17829 6.82149C8.86573 6.50893 8.4418 6.33333 7.99978 6.33333C7.55775 6.33333 7.13383 6.50893 6.82126 6.82149C6.5087 7.13405 6.33311 7.55797 6.33311 8C6.33311 8.44203 6.5087 8.86595 6.82126 9.17851C7.13383 9.49107 7.55775 9.66667 7.99978 9.66667Z"
                    fill="#71717A"
                  />
                </svg>
              </div>
            </div>
          </div>
          {!isPasswordValid ? (
            <div className="text-danger my-2">{passwordError}</div>
          ) : null}
          <div className="mt-2 cursor-pointer " onClick={handleForgotPassword}>
            Forgot password?
          </div>
          <div className="mt-8">
            <button
              onClick={(e) => handleSignIn(e)}
              aria-label="create my account"
              className="focus:ring-2 text-[15px] transition .5s  poppins leading-none text-white  bg-[#ef4823]  rounded hover:bg-[#ca391c] py-4 w-full"
              ref={signInRef}
            >
              Log in
            </button>
            <div className="flex flex-column align-items-center ">
              <div>
                <button
                  onClick={handleGoogleSignIn}
                  aria-label="Continue with google"
                  className="focus:outline-none  py-3.5 px-4 flex items-center w-full mt-3"
                >
                  <svg
                    width={15}
                    height={16}
                    viewBox="0 0 19 20"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M18.9892 10.1871C18.9892 9.36767 18.9246 8.76973 18.7847 8.14966H9.68848V11.848H15.0277C14.9201 12.767 14.3388 14.1512 13.047 15.0812L13.0289 15.205L15.905 17.4969L16.1042 17.5173C17.9342 15.7789 18.9892 13.221 18.9892 10.1871Z"
                      fill="#4285F4"
                    />
                    <path
                      d="M9.68813 19.9314C12.3039 19.9314 14.4999 19.0455 16.1039 17.5174L13.0467 15.0813C12.2286 15.6682 11.1306 16.0779 9.68813 16.0779C7.12612 16.0779 4.95165 14.3395 4.17651 11.9366L4.06289 11.9465L1.07231 14.3273L1.0332 14.4391C2.62638 17.6946 5.89889 19.9314 9.68813 19.9314Z"
                      fill="#34A853"
                    />
                    <path
                      d="M4.17667 11.9366C3.97215 11.3165 3.85378 10.6521 3.85378 9.96562C3.85378 9.27905 3.97215 8.6147 4.16591 7.99463L4.1605 7.86257L1.13246 5.44363L1.03339 5.49211C0.37677 6.84302 0 8.36005 0 9.96562C0 11.5712 0.37677 13.0881 1.03339 14.4391L4.17667 11.9366Z"
                      fill="#FBBC05"
                    />
                    <path
                      d="M9.68807 3.85336C11.5073 3.85336 12.7344 4.66168 13.4342 5.33718L16.1684 2.59107C14.4892 0.985496 12.3039 0 9.68807 0C5.89885 0 2.62637 2.23672 1.0332 5.49214L4.16573 7.99466C4.95162 5.59183 7.12608 3.85336 9.68807 3.85336Z"
                      fill="#EB4335"
                    />
                  </svg>
                  <p className="text-[15px] ml-3 light text-white">
                    Log in with Google
                  </p>
                </button>
              </div>

              <div className="h-[.2px] w-[100%] bg-gray-600"></div>
              <div className="text-[12px] text-gray-400 light mt-3 ">
                You don't have an account ? Click here to{" "}
                <Link to={"/signup"}>
                  <i>
                    <u>Sign up</u>
                  </i>
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
      <ToastContainer
        position="top-center"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />
    </div>
  );
};

export default SignIn;
