import { onAuthStateChanged, signOut } from "firebase/auth";
import React, { useEffect, useState } from "react";
import { auth, db } from "../../config/firebase";
import { Link, useNavigate } from "react-router-dom";
import "react-toastify/dist/ReactToastify.css";
import { doc, onSnapshot } from "firebase/firestore";
import { useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import ProfilePhoto from "../ProfilePhoto";

function Navbar() {
  const [authUser, setAuthUser] = useState(null);
  const { currentUser, currentUserData, isAdmin } = useContext(AuthContext);

  const [screenWidth, setScreenWidth] = useState(window.innerWidth);

  useEffect(() => {
    console.log("Navbar User Data" + typeof JSON.stringify(currentUserData));
    const handleResize = () => {
      setScreenWidth(window.innerWidth);
    };
    // Attach the event listener on component mount
    window.addEventListener("resize", handleResize);
    // Clean up the event listener on component unmount
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const [isMenuClicked, setIsMenuClicked] = useState(false);

  const navigate = useNavigate();

  const handleIsMenuClicked = () => {
    setIsMenuClicked(!isMenuClicked);
  };
  useEffect(() => {
    console.log("navbar : " + typeof currentUserData);
  }, []);
  useEffect(() => {
    setIsMenuClicked(false);

    const listen = onAuthStateChanged(auth, (user) => {
      if (user) {
        setAuthUser(user);
      } else {
        setAuthUser(null);
      }
    });
    return listen();
  }, [currentUser]);
  //Sign out
  const UserSignOut = () => {
    signOut(auth)
      .then(() => {
        console.log("signed out on successfully");
        navigate("/signin");
      })
      .catch((error) => console.log(error));
  };

  return (
    <header className="bg-black text-white py-4 px-6">
    <div className="flex items-center justify-between">
      <Link to="/" className="flex items-center">
        <img
          className="h-8 w-auto mr-2"
          src={`${process.env.PUBLIC_URL}/TechBibleLogoV2.svg`}
          alt="TechBible Logo"
        />
        <span className="font-bold text-xl">TechBible</span>
      </Link>

      <button
        className="block md:hidden text-white focus:outline-none"
        onClick={handleMenuToggle}
      >
        <svg
          className="h-6 w-6 fill-current"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
        >
          <path
            className={`${isMenuOpen ? 'hidden' : 'block'}`}
            fillRule="evenodd"
            clipRule="evenodd"
            d="M3 6h18V5H3v1zm0 6h18v-1H3v1zm0 5h18v-1H3v1z"
          />
          <path
            className={`${isMenuOpen ? 'block' : 'hidden'}`}
            fillRule="evenodd"
            clipRule="evenodd"
            d="M4.293 5.293a1 1 0 0 1 1.414 0L12 10.586l6.293-6.293a1 1 0 0 1 1.414 1.414L13.414 12l6.293 6.293a1 1 0 0 1-1.414 1.414L12 13.414l-6.293 6.293a1 1 0 0 1-1.414-1.414L10.586 12 4.293 5.707a1 1 0 0 1 0-1.414z"
          />
        </svg>
      </button>

      <nav className={`md:flex ${isMenuOpen ? 'block' : 'hidden'}`}>
        <ul className="md:flex items-center space-y-4 md:space-y-0 md:space-x-6">
          <li>
            <Link
              to="/"
              className="block px-2 py-1 rounded hover:bg-white hover:text-black"
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="/about"
              className="block px-2 py-1 rounded hover:bg-white hover:text-black"
            >
              About
            </Link>
          </li>
          <li>
            <Link
              to="/contact"
              className="block px-2 py-1 rounded hover:bg-white hover:text-black"
            >
              Contact
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  </header>

  
  );
}

export default Navbar;
