import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth, provider, db } from "../config/firebase";
import { signInWithPopup } from "firebase/auth";
import { useNavigate } from "react-router-dom";

import { doc, setDoc, Timestamp } from "firebase/firestore";
import "../assets/styles/signin_signup/signin_signup.css";

//integrating open AI in order to generate auto profiles pictures for the users
import { Configuration, OpenAIApi } from "openai";
import { BASE_URL } from "../config/mongo";
import axios from "axios";

const configuration = new Configuration({
  apiKey: process.env.OPENAI_API_KEY,
});
const openai = new OpenAIApi(configuration);

const SignUp = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [uid, setUID] = useState("");
  const [password, setPassword] = useState("");
  const [FullName, setFullName] = useState("");
  // const [passwordError, setPasswordError] = useState("");
  const [generatedImgURL, setGenratedImgURL] = useState();
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [isEmailValid, setIsEmailValid] = useState(true);
  const [isPasswordValid, setIsPasswordValid] = useState(true);

  const signUpRef = useRef(null);

  useEffect(() => {
    function handleKeyDown(event) {
      if (event.code === "Enter") {
        signUpRef.current.click();
      }
    }

    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  });

  //it's basically just the description of the image that you want to be generated
  // const [userPrompt,setUserPrompt] = useState("profile picture on avatar format");

  const [userData, setUserData] = useState(null);

  // const generateImage = async ()=>{
  //   const response =await openai.createImage( {
  //     prompt : "profile picture vector",
  //     n : 1,
  //     size : "256x256"
  //   });
  //   console.log(response.data.data[0].url);
  //   setGenratedImgURL(response.data.data[0].url);

  // }

  // const handleSignUp = (e) => {
  //   e.preventDefault();
  //   // generateImage();
  //   createUserWithEmailAndPassword(auth, email, password)
  //     .then(async (result) => {
  //       const docData = {
  //         uid: result.user.uid,
  //         username: FullName,
  //         bio: "",
  //         interests: [],
  //         folders: [],
  //         photo:
  //           "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnyuq5iI30Yo3TwJF-Sr5PJz0KZLImVddlRw&usqp=CAU",
  //         isAdmin: false,
  //         timestamp: Timestamp.now(),
  //       };
  //       await setDoc(doc(db, "Users", result.user.uid), docData);
  //       navigate("/");
  //     })
  //     .catch((e) => {
  //       if (email === "") {
  //         setEmailError("Field is empty");
  //       } else {
  //         switch (e.code) {
  //           case "auth/email-already-in-use":
  //             setEmailError("Email already in use");
  //             setIsEmailValid(false);
  //             break;
  //           case "auth/invalid-email":
  //             setEmailError("Invalid email");
  //             setIsEmailValid(false);
  //             break;
  //           case "auth/weak-password":
  //             setPasswordError("Password should be at least 6 characters");
  //             setIsPasswordValid(false);
  //             break;
  //           default:
  //             setEmailError("Enter an email");
  //             setPasswordError(e.message);
  //         }
  //       }
  //       // alert(error)
  //     });
  // };

  const handleSignUp = async (e) => {
    e.preventDefault();
    // generateImage();
    try {
      const result = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );

      // const docData = {
      //   uid: result.user.uid,
      //   username: FullName,
      //   bio: "",
      //   interests: [],
      //   folders: [],
      //   photo:
      //     "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnyuq5iI30Yo3TwJF-Sr5PJz0KZLImVddlRw&usqp=CAU",
      //   isAdmin: false,
      //   timestamp: Timestamp.now(),
      // };
      // await setDoc(doc(db, "Users", result.user.uid), docData);

      // setUID(result.user.uid);
      const uid = result.user.uid;
      const photo =
        "https://media.istockphoto.com/id/1422221682/vector/3d-default-profile-wallpaper-8k.jpg?s=170667a&w=0&k=20&c=kHxXnwKP_ApQxSmCBr1qFc6gMclmRlXso7rWOL2Pj04=";
      const response = await axios.post(`${BASE_URL}/signup`, {
        FullName,
        uid,
        photo,
      });
      // Handle successful response
      console.log("User created successfully:", response.data);
      // navigate("/");
      window.location.href = `/`;
    } catch (e) {
      if (email === "") {
        setEmailError("Field is empty");
      } else {
        switch (e.code) {
          case "auth/email-already-in-use":
            setEmailError("Email already in use");
            setIsEmailValid(false);
            break;
          case "auth/invalid-email":
            setEmailError("Invalid email");
            setIsEmailValid(false);
            break;
          case "auth/weak-password":
            setPasswordError("Password should be at least 6 characters");
            setIsPasswordValid(false);
            break;
          default:
            setEmailError("Enter an email");
            setPasswordError(e.message);
        }
      }
      // alert(error)
    }
  };

  const handleGoogleSignUp = () => {
    signInWithPopup(auth, provider).then(async (data) => {
      // const GoogleData = {
      //   uid: data.user.uid,
      //   username: data.user.displayName,
      //   bio: "",
      //   interests: [],
      //   folders: [],
      //   photo: data.user.photoURL,
      //   isAdmin: false,
      //   timestamp: Timestamp.now(),
      // };
      // console.log(data);
      // await setDoc(doc(db, "Users", data.user.uid), GoogleData);
      const FullName = data.user.displayName;
      const uid = data.user.uid;
      const photo = data.user.photoURL;

      const response = await axios.post(`${BASE_URL}/signup`, {
        FullName,
        uid,
        photo,
      });
      window.location.href = `/`;
    });
  };

  return (
    <div className="h-full sign-up bg-[#0D0C12] w-full py-16 px-4">
      <div className="flex flex-col items-center justify-center">
        <div className="bg-[#1A1B1F] shadow rounded-[15px] max-h-[900px] max-w-[505px] w-full py-10 px-[60px]">
          <div className="flex flex-column align-items-center mb-4 ">
            <div className="text-[24px] poppins-medium leading-6 text-white">
              New Account
            </div>
          </div>

          <div className="flex flex-column mb-5">
            <lable className="text-[15px] light leading-none text-white">
              Full name*
            </lable>
            <input
              onChange={(e) => setFullName(e.target.value)}
              role="input"
              type="email"
              className="bg-gray-200 border rounded-[5px] focus:outline-none text-xs font-medium leading-none  h-[36px] text-gray-800 py-3  pl-3 mt-2"
              placeholder="Enter your full name"
            />
          </div>
          <div className="flex flex-column mb-5">
            <lable className="text-[15px] light leading-none text-white">
              E-mail*
